console.log("JavaScript loaded. site.min.js used")

var add_genre_button = document.getElementById("add_genre_button");
if(add_genre_button){
    $(add_genre_button).on('click', function() {
        var genre_list = document.getElementById("genre_list");
        var selected_genre = document.getElementById("genre_select");
        var genre_value = document.getElementById("genre_value");

        var list_item = document.createElement("li");
            list_item.innerText = selected_genre.options[selected_genre.selectedIndex].text + ", ";
            genre_value.value = genre_value.value + selected_genre.options[selected_genre.selectedIndex].text + ", ";
            var selected_option = selected_genre.options[selected_genre.selectedIndex];
            selected_option.hidden = true;
            selected_option.value = genre_value.value;
            genre_list.appendChild(list_item);
    })
}


$(".frontpage-cartbutton").on("click", function(){
    var itemId = $(this).parent().children(".front-page-bookID").text();
    console.log($(this).parent().children(".front-page-bookID").text());
    $.post("Cart", "AddItemToCart", itemId, function(data, status) {
        console.log("Success");
    });
    
})


