console.log("JavaScript loaded. site.min.js used")

$(".dropdown-toggle").on('click', function() {
    console.log("dropdown clicked");
    $(this).parent().toggleClass('open');
    $("#genre-list").val($("ul.genre-menu > li > a.btn.btn-success").text());
});


$("ul.genre-menu > li > a").on('click', function() {
    $(this).toggleClass("btn btn-success");
});



$(".frontpage-cartbutton").on("click", function(){
    var itemId = $(this).parent().children(".front-page-bookID").text();
    console.log($(this).parent().children(".front-page-bookID").text());
    
    $.post("Cart", "AddItemToCart", itemId, function(data, status) {
        console.log("Success");
    });
})
    

